import React from "react";

function Boton({ texto, esBotondeClick, manejarClick }) {
    return (
        <button  
            className={ esBotondeClick ? "boton-click" : "boton-reiniciar" }
            onClick={manejarClick}
            >
            {texto}
        </button>
    );
}

export default Boton;
